import { useEffect, useState } from 'react';

function App() {
  const [pegawai, setPegawai] = useState([]);
  const [form, setForm] = useState({
    nama: '',
    nik: '',
    alamat: '',
    instalasi: ''
  });
  const [editId, setEditId] = useState(null);

  const getData = async () => {
    const res = await fetch('http://localhost:5000/pegawai');
    setPegawai(await res.json());
  };

  useEffect(() => {
    getData();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();

    const url = editId
      ? `http://localhost:5000/pegawai/${editId}`
      : 'http://localhost:5000/pegawai';

    const method = editId ? 'PUT' : 'POST';

    await fetch(url, {
      method,
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(form)
    });

    setForm({ nama: '', nik: '', alamat: '', instalasi: '' });
    setEditId(null);
    getData();
  };

  const handleEdit = (p) => {
    setForm(p);
    setEditId(p.id);
  };

  const handleDelete = async (id) => {
    if (window.confirm('Hapus data?')) {
      await fetch(`http://localhost:5000/pegawai/${id}`, { method: 'DELETE' });
      getData();
    }
  };

  return (
    <div className="container">
      <h2>CRUD Data Pegawai</h2>

      <form onSubmit={handleSubmit}>
        <input placeholder="Nama" value={form.nama}
          onChange={e => setForm({ ...form, nama: e.target.value })} />
        <input placeholder="NIK" value={form.nik}
          onChange={e => setForm({ ...form, nik: e.target.value })} />
        <input placeholder="Alamat" value={form.alamat}
          onChange={e => setForm({ ...form, alamat: e.target.value })} />
        <input placeholder="Instalasi" value={form.instalasi}
          onChange={e => setForm({ ...form, instalasi: e.target.value })} />

        <button>{editId ? 'Update' : 'Simpan'}</button>
      </form>

      <table>
        <thead>
          <tr>
            <th>Nama</th>
            <th>NIK</th>
            <th>Alamat</th>
            <th>Instalasi</th>
            <th>Aksi</th>
          </tr>
        </thead>
        <tbody>
          {pegawai.map(p => (
            <tr key={p.id}>
              <td>{p.nama}</td>
              <td>{p.nik}</td>
              <td>{p.alamat}</td>
              <td>{p.instalasi}</td>
              <td>
                <button onClick={() => handleEdit(p)}>Edit</button>
                <button onClick={() => handleDelete(p.id)}>Hapus</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default App;
